import api from "../../../api";


import Pikachu from '../../../imgs/Pikachu.jpg';
import Pokebola from '../../../imgs/Pokebola.png';

import './Styles.css';





const OurSoluctions = () => {


    //const[Pokemon, setPokemon] = useState([]);

    // const[dataPokemon, setDataPokemon] = useState([]);
    // const[PokemonName, setPokemonName] = useState([]);
